  `age` int(3) DEFAULT NULL,
  `address` varchar(8) DEFAULT NULL,
  `signature` varchar(100) DEFAULT '我是个憨憨！！！',
  PRIMARY KEY (`blogid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

